import * as React from 'react';
import 'nav-frontend-ekspanderbartpanel-style';
import EkspanderbartpanelPure from './ekspanderbartpanel-pure';

/**
 * Self-contained 'Ekspanderbartpanel'.
 * Denne komponenten holder selv styr på om innholdet skal vises eller ikke.
 */

export interface EkspanderbartpanelProps {
    /**
     * Skal komponenten være 'default' åpen
     */
    apen?: boolean;
    /**
     * Callback funksjon for når knappen blir klikket på
     */
    onClick?: (event: React.SyntheticEvent<HTMLButtonElement>) => void;
    /**
     * Tittel/label-tekst
     */
    tittel: string;
    /**
     * Må være en gyldig 'type' prop på Typografi-komponenten, se 'nav-frontend-typografi'
     */
    tittelProps: string;
    /**
     * Hvis komponenten skal brukes på hvit bakgrunn kan denne brukes for å gi den en border
     */
    border?: boolean;
    /**
     * Typen tag som brukes for tittelen på Typografi-komponenten
     */
    tag?: string;
    /**
     * Dersom innholdet skal rendres men ikke vises, når panelet er lukket
     */
    renderContentWhenClosed?: boolean;
    /**
     * Class som blir satt på det ytterste div-elementet
     */
    className?: string;
}

export interface EkspanderbartpanelState {
    apen: boolean;
}

class Ekspanderbartpanel extends React.Component<EkspanderbartpanelProps, EkspanderbartpanelState> {
    static defaultProps: Partial<EkspanderbartpanelProps> = {
        apen: false,
        border: false
    };

    constructor(props) {
        super(props);

        this.state = {
            apen: this.props.apen!
        };

        this.handleClick = this.handleClick.bind(this);
    }

    handleClick(event: React.SyntheticEvent<HTMLButtonElement>): void {
        event.preventDefault();
        this.setState({ apen: !this.state.apen });
        if (this.props.onClick) {
            this.props.onClick(event);
        }
    }

    render() {
        const { tag, tittel, tittelProps, ...renderProps } = this.props;
        return (
            <EkspanderbartpanelPure
                {...renderProps}
                apen={this.state.apen}
                onClick={this.handleClick}
                tittel={tittel}
                tittelProps={tittelProps}
                tag={tag}
            />
        );
    }
}

export default Ekspanderbartpanel;
export { default as EkspanderbartpanelPure } from './ekspanderbartpanel-pure';
export { default as EkspanderbartpanelBase } from './ekspanderbartpanel-base';
export { default as EkspanderbartpanelBasePure } from './ekspanderbartpanel-base-pure';
