import * as PT from 'prop-types';
import * as React from 'react';
import { guid } from 'nav-frontend-js-utils';
import 'nav-frontend-skjema-style';
import * as classNames from 'classnames';

const cls = (className) => classNames('skjemaelement skjemaelement--horisontal', className);

export interface RadioProps extends React.InputHTMLAttributes<HTMLInputElement>{
    /**
     * ClassName
     */
    className?: string;
    /**
     * Label for radiobutton
     */
    label: React.ReactNode;
    /**
     * Id for radiobutton, hvis id ikke er satt brukes en tilfeldig guid
     */
    id?: string;
    /**
     * Name for radiobutton gruppe
     */
    name: string;
    /**
     * Referanse til selve radioknappen. Brukes for eksempel til å sette fokus
     */
    radioRef?: () => any;
}

/**
 * Radio
 */
class Radio extends React.Component<RadioProps> {

    render() {
        const { className, name, label, id, radioRef, ...other } = this.props;
        const inputId = id || guid();
        // tslint:disable:react-a11y-role-has-required-aria-props
        return (
            <div className={cls(className)}>
                <input
                    type="radio"
                    name={name}
                    className="skjemaelement__input radioknapp"
                    id={inputId}
                    ref={radioRef}
                    {...other}
                />
                <label className="skjemaelement__label" htmlFor={inputId}>{label}</label>
            </div>
        );
    }
}

(Radio as React.ComponentClass).propTypes = {
    /**
     * ClassName
     */
    className: PT.string,
    /**
     * Label for radiobutton
     */
    label: PT.node.isRequired,
    /**
     * Id for radiobutton, hvis id ikke er satt brukes en tilfeldig guid
     */
    id: PT.string,
    /**
     * Name for radiobutton gruppe
     */
    name: PT.string.isRequired,
    /**
     * Referanse til selve radioknappen. Brukes for eksempel til å sette fokus
     */
    radioRef: PT.func
};

(Radio as React.ComponentClass).defaultProps = {
    className: undefined,
    id: undefined,
    radioRef: undefined
};

export default Radio;
